import React from "react";
import ReactDOM from "react-dom";
import mapboxgl from "mapbox-gl";
import MapBoxGLDraw from "mapbox-gl-draw";

mapboxgl.accessToken =
  "pk.eyJ1IjoiamFjcXVlbGluZWNoZW4iLCJhIjoiY2s2ZHB5Y2RxMDkxbzNkbXA2bXVzM3JvbiJ9.pUyDxtMWjGqmGgX4JAdL7g";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lng: 5,
      lat: 34,
      zoom: 4
    };
  }

  updateRoute() {
    this.removeRoute(); // overwrite any existing layers
    var data = this.draw.getAll();
    var answer = document.getElementById("calculated-line");
    var lastFeature = data.features.length - 1;
    var coords = data.features[lastFeature].geometry.coordinates;
    var newCoords = coords.join(";");
    getMatch(newCoords);
  }

  getMatch(e) {
    var url =
      "https://api.mapbox.com/directions/v5/mapbox/cycling/" +
      e +
      "?geometries=geojson&steps=true&&access_token=" +
      mapboxgl.accessToken;

    var req = new XMLHttpRequest();
    console.log("req", req);
    req.responseType = "json";
    req.open("GET", url, true);
    req.onload = function() {
      var jsonResponse = req.response;
      console.log("jsonReponse", jsonResponse);
      var distance = jsonResponse.routes[0].distance * 0.001;
      var duration = jsonResponse.routes[0].duration / 60;
      document.getElementById("calculated-line").innerHTML =
        "Distance: " +
        distance.toFixed(2) +
        " km<br>Duration: " +
        duration.toFixed(2) +
        " minutes";
      var coords = jsonResponse.routes[0].geometry;
      // add the route to the map
      addRoute(coords);
      getInstructions(jsonResponse.routes[0]);
    };
    req.send();
  }

  componentDidMount() {
    const map = new mapboxgl.Map({
      container: this.mapContainer,
      style: "mapbox://styles/mapbox/streets-v11",
      center: [this.state.lng, this.state.lat],
      zoom: this.state.zoom
    });

    const draw = new MapBoxGLDraw({
      displayControlsDefault: false,
      controls: {
        line_string: true,
        trash: true
      },
      styles: [
        // ACTIVE (being drawn)
        // line stroke
        {
          id: "gl-draw-line",
          type: "line",
          filter: [
            "all",
            ["==", "$type", "LineString"],
            ["!=", "mode", "static"]
          ],
          layout: {
            "line-cap": "round",
            "line-join": "round"
          },
          paint: {
            "line-color": "#3b9ddd",
            "line-dasharray": [0.2, 2],
            "line-width": 4,
            "line-opacity": 0.7
          }
        },
        // vertex point halos
        {
          id: "gl-draw-polygon-and-line-vertex-halo-active",
          type: "circle",
          filter: [
            "all",
            ["==", "meta", "vertex"],
            ["==", "$type", "Point"],
            ["!=", "mode", "static"]
          ],
          paint: {
            "circle-radius": 10,
            "circle-color": "#FFF"
          }
        },
        // vertex points
        {
          id: "gl-draw-polygon-and-line-vertex-active",
          type: "circle",
          filter: [
            "all",
            ["==", "meta", "vertex"],
            ["==", "$type", "Point"],
            ["!=", "mode", "static"]
          ],
          paint: {
            "circle-radius": 6,
            "circle-color": "#3b9ddd"
          }
        }
      ]
    });

    // add the draw tool to the map
    map.addControl(draw);

    // add create, update, or delete actions
    map.on("draw.create", this.updateRoute);
    map.on("draw.update", this.updateRoute);
    map.on("draw.delete", this.removeRoute);

    // Storing new coordinate (whatever the user interacts with)
    map.on("move", () => {
      this.setState({
        lng: map.getCenter().lng.toFixed(4),
        lat: map.getCenter().lat.toFixed(4),
        zoom: map.getZoom().toFixed(2)
      });
    });
  }

  render() {
    return (
      <div>
        <div className="sidebarStyle">
          <div>
            Longitude: {this.state.lng} | Latitude: {this.state.lat} | Zoom:{" "}
            {this.state.zoom}
          </div>
        </div>
        <div ref={el => (this.mapContainer = el)} className="mapContainer" />
      </div>
    );
  }
}

ReactDOM.render(<App />, document.getElementById("app"));
